import React from 'react'

// import styles from './Attraction.module.css'

type Props = {
    name: string,
    tags: string[],
    number_of_reviews: number,
    rating: number,
    imgUrl?: string,

    titleSize?: string,
    titleColor?: string
}

//Exapmle of style using object (for more readable code)
const cardStyle: React.CSSProperties = {
    boxShadow: '0 4px 8px 0 rgba(0,0,0,0.2)',
    transition: '0.3s',
    width: '25em',
    marginRight: '2em',
    marginBottom: '2em',
    minHeight: '42em',
}
const googleKey = 'AIzaSyBPWFwHrsgzYw36bl-ecghaFEqNuRuGDUg'

const Attraction = (props: Props) => {
    const imgFound = props.imgUrl? `https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&maxheight=400&key=${googleKey}&photoreference=${props.imgUrl}` : 'https://www.publicdomainpictures.net/pictures/280000/velka/not-found-image-15383864787lu.jpg'
    // const imgFound = `https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&photoreference=oUSSDrYjWmrNfeEHSGSc3FyhNLlBU&key=${googleKey}`
    return <>
        <div style={cardStyle}>
            <img alt="Avatar" src={imgFound} style={{ objectFit: 'cover', height: '25em', width: "100%" }} />
            <div style={{ fontSize: '2em' }}>
                <h4 style={{
                    padding: '2px 16px',
                    color: props.titleColor ? props.titleColor : 'black',
                    fontSize: props.titleSize ? props.titleSize : '2em'
                }}
                >
                    <b>{props.name}</b></h4>
                <p style={{ fontSize:'0.8em' }}>Rating: {props.rating}   |   {props.number_of_reviews} Reviews</p>
                <p style={{ color: 'grey',fontSize:'0.7em' }}>{props.tags.join(', ')} </p>
            </div>
        </div>
    </>
}
export default Attraction

