		.then(response => response.json())
		.then(data => console.log(JSON.stringify(data)))

	// const attractionsData: attr[] = require('../data/attractions_museums_tlv.json')

	const chosenHotel = Props.hotel ? Props.hotel : 'default'

	return <>
		<div style={{ textAlign: 'center', padding: '2em', fontFamily: 'Monospace' }}>
			<Head>
				<title>Bridgify</title>
				{/* Loading the google places library */}
				<script src={googleScript}></script>
			</Head>
			<h1 style={{ color: 'purple', paddingBottom: '0.5em', fontSize: '3em' }}>Bridgify!</h1>
			<div style={{ placeContent: 'center', display: 'flex', flexWrap: 'wrap' }}>
				{attractionsData.map(attr =>
					<Attraction
						key={attr.id}
						name={attr.name}
						number_of_reviews={attr.number_of_reviews}
						rating={attr.rating}
						tags={attr.tags}
						imgUrl={attr.main_photo_url}
						titleSize={hotelsDesignDic[chosenHotel].titleSize}
						titleColor={hotelsDesignDic[chosenHotel].titleColor}
					></Attraction>)}
			</div>
		</div>
	</>
}

export default Home

