const Home = () => {
	const attractionsData: attr[] = require('../data/attractions_museums_tlv.json')
	return <>
		<div style={{ textAlign: 'center', padding: '2em',fontFamily:'Monospace'}}>
			<Head>
				<title>Bridgify</title>
				{/* <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css" integrity="sha384-Gn5384xqQ1aoWXA+058RXPxPg6fy4IWvTNh0E263XmFcJlSAwiGgFAW/dAiS6JXm" crossorigin="anonymous"></link> */}
				{/* Loading the google places library */}
				{/* <script src={googleScript}></script> */}
			</Head>
			<h1 style={{ color: 'purple' }}>Bridgify!</h1>
			<Link href="/attractions/ski"><a> Go Ski!</a></Link>

			{/* Using bootstrap card's attributes, while sending the following props */}
			{/* <CardGroup style={{ placeContent: 'center' }}> */}
			<div style={{            display:'flex',
            flexWrap:'wrap'}}>

				{attractionsData.map(attr =>
					<Attraction
						key={attr.id}
						name={attr.name}
						number_of_reviews={attr.number_of_reviews}
						rating={attr.rating}
						tags={attr.tags}
						imgUrl={attr.main_photo_url}
						></Attraction>)}
			{/* </CardGroup> */}
						</div>
		</div>
	</>
}

