import React from 'react'
import Link from 'next/link'
import styles from './index.module.css'

declare var google: any;
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import Attraction from '../components/Attraction'
import { CardGroup } from 'react-bootstrap';

import Head from "next/head";

type attr = {
	id: number,
	name: string,
	tags: string[],
	number_of_reviews: number,
	main_photo_url: string
	rating: number,
	location_lng: number,
	location_lat: number,
	opening_hours: number[],
	created: string,
	source: string,
	address: string,
	about: number,
	order_page: any,
	is_free: boolean,
	price: any
}

// AIzaSyBPWFwHrsgzYw36bl-ecghaFEqNuRuGDUg
// Google Places search API call
// https://stackoverflow.com/questions/37874018/retrieve-points-of-interests-attractions-tourist-places-of-a-place-country-usi
// https://maps.googleapis.com/maps/api/place/textsearch/json?query=new+york+city+point+of+interest&language=en&key=AIzaSyATrA8jcd0kjYz7cv-hjACyq10r3MMhe7A
const attractionsData: attr[] = require('../data/attractions_museums_tlv.json')

// __=---------------------------------------------------------------------------------

const request = {
    location: new google.maps.LatLng(51.5287352, -0.3817841),
    radius: 5000,
    type: ['restaurant']
};

const results = [];
const places = document.getElementById('places');
const service = new google.maps.places.PlacesService(places);

const callback = (response, status, pagination) => {
    if (status == google.maps.places.PlacesServiceStatus.OK) {
        results.push(...response);
    }

    if (pagination.hasNextPage) {
        setTimeout(() => pagination.nextPage(), 2000);
    } else {
        displayResults();
    }
}

const displayResults = () => {
    results.filter(result => result.rating)
            .sort((a, b) => a.rating > b.rating ? -1 : 1)
            .forEach(result => {
                places.innerHTML += `<li>${result.name} - ${result.rating}</li>`;
            });
}

service.nearbySearch(request, callback);


// __=---------------------------------------------------------------------------------

const indexPage = () =>
	<div className={styles.body}>
		<Head>
			<title>My page title</title>
			<meta name="viewport" content="initial-scale=1.0, width=device-width" />
			<script src="https://maps.googleapis.com/maps/api/js?key=IzaSyBPWFwHrsgzYw36bl-ecghaFEqNuRuGDUg&libraries=places"></script>
		</Head>
		<h1>Hello Bridgify!! :)</h1>
		<p>go to <Link href="/auth"><a>Auth</a></Link></p>
		<CardGroup style={{ placeContent: 'center' }}>
			{attractionsData.map(attr =>
				<Attraction
					key={attr.id}
					name={attr.name}
					number_of_reviews={attr.number_of_reviews}
					rating={attr.rating}
					tags={attr.tags}
					imgUrl={attr.main_photo_url}
				></Attraction>)}
		</CardGroup>
	</div>

export default indexPage
