import React from 'react'
import Head from "next/head";
import Link from 'next/link'

//----->in-app components & styles
import Attraction from '../components/Attraction'
// import styles from './index.module.css'can
import { attr } from '../interfaces/'


// export async function getStaticProps() {
// 	// Get external data from the file system, API, DB, etc.
// 	const attractionsData: attr[] = require('../data/attractions_museums_tlv.json')
// 	// The value of the `props` key will be passed to the `Home` component
// 	return {
// 		props: { attractionsData }
// 	}
// }

//example of prettier in-line style using oobjects
const inlineStylesWithObject :React.CSSProperties = { placeContent: 'center', display: 'flex', flexWrap: 'wrap' }

const Home = () => {
	const attractionsData: attr[] = require('../data/attractions_museums_tlv.json')
	return <>
		<div style={{ textAlign: 'center', padding: '2em', fontFamily: 'Monospace' }}>
			<Head>
				<title>Bridgify</title>
				{/* Loading the google places library */}
				{/* <script src={googleScript}></script> */}
			</Head>
			<h1 style={{ color: 'purple', paddingBottom: '0.5em', fontSize: '3em' }}>Bridgify!</h1>
			<div style={{ placeContent: 'center', display: 'flex', flexWrap: 'wrap' }}>
				{attractionsData.map(attr =>
					<Attraction
						key={attr.id}
						name={attr.name}
						number_of_reviews={attr.number_of_reviews}
						rating={attr.rating}
						tags={attr.tags}
						imgUrl={attr.main_photo_url}
					></Attraction>)}
			</div>
		</div>
	</>
}

