//----->react & next
import React from 'react'
import Head from "next/head"
//----->in-app components & styles
import AttractionCard from '../components/AttractionCard'
import { Attraction } from '../interfaces/'
import { GetServerSideProps, GetStaticProps } from 'next'

type designProps = {
	titleSize: string,
	titleColor: string
}

const hotelsDesignDic: { [key: string]: designProps } = {
	['default']: {
		titleSize: '1em',
		titleColor: 'black'
	},
	['london']: {
		titleSize: '1em',
		titleColor: 'pink'
	},
	['lisbon']: {
		titleSize: '0.8em',
		titleColor: 'green'
	}
};



const googleKey = 'AIzaSyBPWFwHrsgzYw36bl-ecghaFEqNuRuGDUg'
const googleURL = `https://maps.googleapis.com/maps/api/place/nearbysearch/json?`

const params = new URLSearchParams({
		location: '38.7198852,-9.1402976',
	radius: '1500',
	keyword: 'attraction',
	key: googleKey
}).toString()


// getServerSideProps (Server-side Rendering): Fetch data on each request, to get the most updated attractions
export const getServerSideProps: GetServerSideProps = async () => {
	// Call an external API endpoint to get attractions.
	const res = await fetch(googleURL + params)
	let data = await res.json()
	const attractions: Attraction[] = data['results']

	// 'Home' component will receive resultsData as a prop by returning it
	return {
		props: {
			attractions
		}
	}
}

type Props = {
	hotel?: string
	attractions?: any
}
const Home = ({ attractions, hotel }: Props) => {

	const chosenHotel = hotel ? hotel : 'default'
	return <>
		<div style={{ textAlign: 'center', padding: '2em', fontFamily: 'Monospace' }}>
			<Head>
				<title>Bridgify</title>
			</Head>
			<h1 style={{ color: 'purple', paddingBottom: '0.5em', fontSize: '3em' }}>Bridgify!</h1>
			<div style={{ placeContent: 'center', display: 'flex', flexWrap: 'wrap' }}>
				{attractions.map((attr, index) => {
					return <AttractionCard
						key={attr.place_id}
						name={attr.name}
						number_of_reviews={attr.user_ratings_total}
						rating={attr.rating}
						tags={attr.types}
						photoRef={attr.photos ? attr.photos[0] ? attr.photos[0].photo_reference : null : null}
						titleSize={hotelsDesignDic[chosenHotel].titleSize}
						titleColor={hotelsDesignDic[chosenHotel].titleColor}
					></AttractionCard>
				})}
			</div>
		</div>
	</>
}

export default Home
