import React from 'react'

import { Card } from 'react-bootstrap';
import styles from './Attraction.module.css'

type Props = {
    name: string,
    tags: string[],
    number_of_reviews: number,
    rating: number,
    imgUrl: string
}

const Attraction = (props: Props) =>
    <div>
        <Card className={styles.card}>
            <Card.Img
                src={props.imgUrl}
                className={styles.attrImg}
            />
            <Card.Body>
                {/* <Card.Text> */}{/* </Card.Text> */}
                <h2 className={styles.attrName}> {props.name} </h2>
                <div className={styles.attrDesc}>
                    <p>Rating: {props.rating}   |   {props.number_of_reviews} Reviews</p>
                    <p className={styles.tags}>{props.tags.join(', ')} </p>
                </div>
            </Card.Body>
        </Card>
    </div>

export default Attraction

