//----->react & next
import React, { useState } from 'react';

import Head from "next/head";


//----->in-app components & styles
import Attraction from '../components/Attraction'
// import styles from './index.module.css'can
import { attr, attrResult } from '../interfaces/'
import { error } from 'console';
import { GetStaticProps } from 'next';

type designProps = {
	titleSize: string,
	titleColor: string
}

const hotelsDesignDic: { [key: string]: designProps } = {
	['default']: {
		titleSize: '1em',
		titleColor: 'black'
	},
	['hilton']: {
		titleSize: '1em',
		titleColor: 'pink'
	},
	['royal']: {
		titleSize: '0.8em',
		titleColor: 'green'
	}
};

type Props = {
	hotel?: string
	resultsData?: any
}

// https://developers.google.com/maps/documentation/javascript/places
// The Places service is a self-contained library, separate from the main Maps JavaScript API code. 
// To use the functionality contained within this library, you must first load it using the libraries parameter in the Maps API bootstrap URL

const googleKey = 'AIzaSyBPWFwHrsgzYw36bl-ecghaFEqNuRuGDUg'
const googleScript = `https://maps.googleapis.com/maps/api/js?key=${googleKey}&libraries=places`
// const googleURL = `https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=32.0715658,34.772792&radius=1500&type=bars&key=AIzaSyBPWFwHrsgzYw36bl-ecghaFEqNuRuGDUg`
const googleURL = `https://maps.googleapis.com/maps/api/place/nearbysearch/json?`

// This function gets called at build time on server-side.
export const getStaticProps: GetStaticProps = async () => {
	// Call an external API endpoint to get posts.
	// let resultsData =  require('../data/resultsFromAPI.json')['results']
	// TO OVERCOME CORS ERROR using some tool---> https://proxyurl/googleURL 
	const req = {
		location: '32.0715658,34.772792',
		radius:'1500',
		type:'bars',
		key:googleKey
	}
	const res = await fetch(googleURL + req)
	let data = await res.json()
	const resultsData: attrResult[] = data['results']
	console.log(resultsData);
	
	// 'Home' component will receive resultsData as a prop by returning it
	return {
		props: {
			resultsData
		}
	}
}


const Home = ({ resultsData, hotel }: Props) => {
	console.log(resultsData);
	
	const chosenHotel = hotel ? hotel : 'default'
	return <>
		<div style={{ textAlign: 'center', padding: '2em', fontFamily: 'Monospace' }}>
			<Head>
				<title>Bridgify</title>
				<script defer src={googleScript} />
			</Head>
			<h1 style={{ color: 'purple', paddingBottom: '0.5em', fontSize: '3em' }}>Bridgify!</h1>
			<div style={{ placeContent: 'center', display: 'flex', flexWrap: 'wrap' }}>
				{resultsData.map((attr, index) => {
					return <Attraction
						key={attr.place_id}
						name={attr.name}
						number_of_reviews={attr.user_ratings_total}
						rating={attr.rating}
						tags={attr.types}
						imgUrl={attr.photos ? attr.photos[0] ? attr.photos[0].photo_reference : null : null}
						titleSize={hotelsDesignDic[chosenHotel].titleSize}
						titleColor={hotelsDesignDic[chosenHotel].titleColor}
					></Attraction>
				})}
			</div>
		</div>
	</>
}

export default Home
