import React, { useState } from 'react';

import Head from "next/head";


//----->in-app components & styles
import Attraction from '../components/Attraction'
// import styles from './index.module.css'can
import { attr, attrResult } from '../interfaces/'

type designProps = {
	titleSize: string,
	titleColor: string
}

const hotelsDesignDic: { [key: string]: designProps } = {
	['default']: {
		titleSize: '1em',
		titleColor: 'black'
	},
	['hilton']: {
		titleSize: '1em',
		titleColor: 'pink'
	},
	['royal']: {
		titleSize: '0.8em',
