import React from 'react'

import { Card } from 'react-bootstrap';
import styles from './Attraction.module.css'

type Props = {
    name: string,
    tags: string[],
    number_of_reviews: number,
    rating: number,
    imgUrl: string
}

//Styles
const cardStyle :React.CSSProperties = {
    boxShadow: '0 4px 8px 0 rgba(0,0,0,0.2)', 
    transition: '0.3s', 
    width: '30em', 
    marginRight: '2em',
    marginBottom: '2em',
    minHeight: '42em',
}

const Attraction = (props: Props) =>
    <>
        <div style={cardStyle}>
            <img alt="Avatar" src={props.imgUrl} style={{ objectFit: 'cover', height: '25em',width:"100%" }} />
            <div style={{ fontSize:'2em' }}>
                <h4 style={{ padding: '2px 16px' }}><b>{props.name}</b></h4>
                <p>Rating: {props.rating}   |   {props.number_of_reviews} Reviews</p>
                <p style={{ color: 'grey' }}>{props.tags.join(', ')} </p>
            </div>
        </div>
    </>
export default Attraction

