import React from 'react'

import { Card } from 'react-bootstrap';
import styles from '../pages/index.module.css'

type propsType = {
    name: string,
    tags: string[],
    number_of_reviews: number,
    rating: number,
    imgUrl: string
}

const Attraction = (props: propsType) =>
    <div>
        <Card style={{ width: '40em', marginRight: "2em", marginBottom: "2em", minHeight:'42em' }}>
            <Card.Img
                // height='100%'
                src={props.imgUrl}
                // style={{ minHeight: "100%", minWidth: "100%" }}
                />
            <Card.Body>
                {/* <Card.Text> */}
                    <h2 className={styles.attrName}> {props.name} </h2>
                    <div className={styles.attrDesc}>
                        <p>Rating: {props.rating}   |   {props.number_of_reviews} Reviews</p>
                        <p className={styles.tags}>{props.tags.join(', ')} </p>
                    </div>
                {/* </Card.Text> */}
            </Card.Body>
        </Card>
    </div>


export default Attraction
