//react & next
import React from 'react'
import Head from "next/head";
//bootstrap
import { CardGroup } from 'react-bootstrap';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
//in-app components & styles
import Attraction from '../components/Attraction'
import styles from './index.module.css'
import {attr} from '../interfaces/'


declare var google: any;
// 
// Google Places search API call
// https://stackoverflow.com/questions/37874018/retrieve-points-of-interests-attractions-tourist-places-of-a-place-country-usi
// https://maps.googleapis.com/maps/api/place/textsearch/json?query=new+york+city+point+of+interest&language=en&key=AIzaSyATrA8jcd0kjYz7cv-hjACyq10r3MMhe7A
const attractionsData: attr[] = require('../data/attractions_museums_tlv.json')

const googleKey = 'AIzaSyBPWFwHrsgzYw36bl-ecghaFEqNuRuGDUg'
const googleScript = `https://maps.googleapis.com/maps/api/js?key=${googleKey}&libraries=places&callback=initMap`

const indexPage = () => {
	// __=---------------------------------------------------------------------------------

	var request = {
		location: new google.maps.LatLng(52.48, -1.89),
		radius: '500',
		types: ['store']
	};

	var container = document.getElementById('results');

	var service = new google.maps.places.PlacesService(container);
	service.nearbySearch(request, callback);

	function callback(results, status) {

		if (status == google.maps.places.PlacesServiceStatus.OK) {

			for (var i = 0; i < results.length; i++) {

				container.innerHTML += results[i].name + '<br />';
			}
		}
	}

	// __=---------------------------------------------------------------------------------

	return (

		<div className={styles.body}>
			<Head>
				<title>Bridgify</title>
				{/* Loading the google places library */}
				<script src={googleScript}></script>
			</Head>
			<h1 className={styles.mainTitle}>Bridgify!</h1>
			{/* <p>go to <Link href="/auth"><a>Auth</a></Link></p> */}
			
			{/* Using bootstrap card's attributes, while sending the following props */}
			<CardGroup className={styles.cardGroup}>
				{attractionsData.map(attr =>
					<Attraction
						key={attr.id}
						name={attr.name}
						number_of_reviews={attr.number_of_reviews}
						rating={attr.rating}
						tags={attr.tags}
						imgUrl={attr.main_photo_url}
					></Attraction>)}
			</CardGroup>
		</div>
	)
}

export default indexPage
