import React from 'react'

import { Card } from 'react-bootstrap';
import styles from './Attraction.module.css'

type Props = {
    name: string,
    tags: string[],
    number_of_reviews: number,
    rating: number,
    imgUrl: string
}

const Attraction = (props: Props) =>
    <div>
        <div style={{boxShadow: '0 4px 8px 0 rgba(0,0,0,0.2)',transition: '0.3s', width: '40%'}}>
            <img src="img_avatar.png" alt="Avatar" style={{width:"100%"}}/>
                <div style={{padding: '2px 16px'}}>
                    <h4><b>John Doe</b></h4>
                    <p>Architect & Engineer</p>
                </div>
        </div>

            <Card style={{
                width: '40em',
                marginRight: '2em',
                marginBottom: '2em',
                minHeight: '42em',
            }}>
                <Card.Img
                    src={props.imgUrl}
                    style={{ objectFit: 'cover', minHeight: '30em' }}
                />
                <Card.Body>
                    <h2 style={{ fontSize: '2.4em' }}> {props.name} </h2>
                    <div style={{ fontSize: '1.4em' }}>
                        <p>Rating: {props.rating}   |   {props.number_of_reviews} Reviews</p>
                        <p style={{ color: 'grey' }}>{props.tags.join(', ')} </p>
                    </div>
                </Card.Body>
            </Card>
        </div>

export default Attraction

