import { NextApiRequest, NextApiResponse } from 'next'
import ReactDOMServer from 'react-dom/server';

import Home from '..'
import React from 'react'
import { Attraction } from '../../interfaces';


const googleKey = 'AIzaSyBPWFwHrsgzYw36bl-ecghaFEqNuRuGDUg'
const googleURL = `https://maps.googleapis.com/maps/api/place/nearbysearch/json?`

const params = new URLSearchParams({
  location: '38.7198852,-9.1402976',
	radius: '1500',
	query : 'attraction',
	key: googleKey
}).toString()

// const paramsAsString = Object.entries(paramsAsObject).map(([key, val]) => `${key}=${val}`).join('&')

const hilton = async (req: NextApiRequest, res: NextApiResponse) => {

  const googleRes = await fetch(googleURL + params)
	let data = await googleRes.json()
  const resultsData: Attraction[] = data['results']
  
  const HTMLString :string = ReactDOMServer.renderToString(<Home resultsData={resultsData} hotel={'hilton'}/>);
  res.status(200).send(JSON.stringify (HTMLString))
  // res.status(200).send(HTMLString)
}

export default hilton
